mapper.select:
SELECT *
FROM TECB_BOARD_DEFAULT_SLAVE
WHERE SEQ = #{seq}
AND BOARD_DEFAULT_MASTER_SEQ = #{boardDefaultMasterSeq}

SELECT @ROWNUM:=@ROWNUM+1 as row, ta.SEQ, ta.TITLE, ta.HIT_CNT, ta.NOTICE_FLAG, ta.EDIT_DATE, ta.LANG_TYPE, ta.ONECLICK_GCD, ta.ONECLICK_GNM FROM TECB_BOARD_DEFAULT_SLAVE ta, (SELECT @ROWNUM := #{offset}) TMP WHERE ta.DELETE_FLAG = 'N' AND ta.BOARD_DEFAULT_MASTER_SEQ = #{masterSeq}
AND ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY REG_DATE DESC
LIMIT #{limit} OFFSET #{offset}

SELECT FILE_NAME, FILE_PATH
FROM TECC_BOARD_DEFAULT_FILE
WHERE DELETE_FLAG = 'N'
AND BOARD_DEFAULT_SLAVE_SEQ = #{boardDefaultSlaveSeq} 
AND SEQ = #{seq}

mapper.update:
UPDATE TECC_BOARD_DEFAULT_FILE
SET DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND BOARD_DEFAULT_SLAVE_SEQ = #{slaveSeq}
AND THUM_FLAG = 'N'
AND SEQ in

		#{item}

UPDATE TECB_BOARD_DEFAULT_SLAVE
SET DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND BOARD_DEFAULT_MASTER_SEQ = #{boardDefaultMasterSeq}
AND SEQ = #{seq}

mapper.select:
SELECT * FROM(
SELECT @ROWNUM:=@ROWNUM+1 as row, ta.SEQ, ta.TITLE, ta.CONTENT, ( SELECT COUNT(1) FROM TECC_BOARD_DEFAULT_FILE WHERE DELETE_FLAG = 'N' AND BOARD_DEFAULT_SLAVE_SEQ = ta.SEQ AND THUM_FLAG = 'N' ) as FILE_CNT, ta.HIT_CNT, ta.EDIT_DATE, ta.REG_DATE, ta.ONECLICK_GCD, ta.ONECLICK_GNM FROM TECB_BOARD_DEFAULT_SLAVE ta, (SELECT @ROWNUM := 0) TMP WHERE ta.DELETE_FLAG = 'N' AND ta.BOARD_DEFAULT_MASTER_SEQ = #{masterSeq} AND ta.LANG_TYPE = #{langType}
AND ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY ta.REG_DATE )SUB ORDER BY SUB.REG_DATE DESC, SUB.row DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE TECB_BOARD_DEFAULT_SLAVE ta
SET ta.HIT_CNT = ta.HIT_CNT+1
WHERE ta.DELETE_FLAG = 'N'
AND ta.BOARD_DEFAULT_MASTER_SEQ = #{boardDefaultMasterSeq}
AND ta.SEQ = #{seq}

mapper.select:
(
	SELECT SEQ, TITLE, 'pre' as TYPE
	FROM TECB_BOARD_DEFAULT_SLAVE
	WHERE #{seq} > SEQ
	AND BOARD_DEFAULT_MASTER_SEQ = #{boardDefaultMasterSeq}
	AND DELETE_FLAG = 'N'
	AND LANG_TYPE = #{langType}			
	ORDER BY SEQ DESC
	LIMIT 1
)
UNION ALL
(
	SELECT SEQ, TITLE, 'next' as TYPE
	FROM TECB_BOARD_DEFAULT_SLAVE
	WHERE SEQ > #{seq}
	AND BOARD_DEFAULT_MASTER_SEQ = #{boardDefaultMasterSeq}
	AND DELETE_FLAG = 'N'
	AND LANG_TYPE = #{langType}
	ORDER BY SEQ ASC
	LIMIT 1
)

mapper.update:
UPDATE TECC_BOARD_DEFAULT_FILE ta
SET ta.DOWN_CNT = ta.DOWN_CNT +1
WHERE ta.DELETE_FLAG = 'N'
AND ta.SEQ = #{seq}
AND ta.BOARD_DEFAULT_SLAVE_SEQ = #{boardDefaultSlaveSeq}
