mapper.update:
UPDATE TEAA_BOARD_NOTICE
SET DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND SEQ = #{seq}

UPDATE TEAB_BOARD_NOTICE_FILE
SET 
	DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE BOARD_NOTICE_SEQ = #{noticeSeq}
AND SEQ in

		#{item}

mapper.select:
SELECT @ROWNUM:=@ROWNUM+1 as row, ta.SEQ, ta.TITLE, ta.NOTICE_NUMBER, ta.HIT_CNT, ( SELECT tba.INFO_NAME FROM TABC_CODE_SLAVE_INFO tba WHERE tba.MASTER_CODE = 'center' AND tba.DELETE_FLAG = 'N' AND tba.SLAVE_CODE = ta.CODE_CENTER_TYPE AND tba.CODE_LANG_TYPE = #{codeLangType} ) as CODE_CENTER_NAME, ( SELECT COUNT(1) FROM TEAB_BOARD_NOTICE_FILE WHERE DELETE_FLAG = 'N' AND BOARD_NOTICE_SEQ = ta.SEQ ) as FILE_CNT, ta.EDIT_DATE, ta.REG_DATE, ta.LOGIN_YN FROM TEAA_BOARD_NOTICE ta, (SELECT @ROWNUM := #{offset}) TMP WHERE ta.DELETE_FLAG = 'N' AND ta.CODE_LANG_TYPE = #{codeLangType}
AND ta.LOGIN_YN IN('N','Y')
AND ta.LOGIN_YN = 'N'
AND ta.CODE_CENTER_TYPE = #{codeCenterType}
AND ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
AND ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( ta.TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR ta.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY REG_DATE DESC
LIMIT #{limit} OFFSET #{offset}

SELECT ta.SEQ, ( SELECT tba.INFO_NAME FROM TABC_CODE_SLAVE_INFO tba WHERE tba.MASTER_CODE = 'center' AND tba.DELETE_FLAG = 'N' AND tba.SLAVE_CODE = ta.CODE_CENTER_TYPE AND tba.CODE_LANG_TYPE = #{codeLangType} ) as CODE_CENTER_NAME,
ta.TITLE, ta.NOTICE_NUMBER, ta.CONTENT,
ta.HIT_CNT, (SELECT NAME from TAAA_ADMIN_USER WHERE USER_ID = ta.EDIT_USER_ID) as EDIT_USER_NAME, ta.EDIT_DATE FROM TEAA_BOARD_NOTICE ta WHERE ta.DELETE_FLAG = 'N' AND ta.CODE_LANG_TYPE = #{codeLangType} AND ta.SEQ = #{seq}

mapper.update:
UPDATE TEAB_BOARD_NOTICE_FILE ta
SET ta.DOWN_CNT = ta.DOWN_CNT +1
WHERE ta.DELETE_FLAG = 'N'
AND ta.SEQ = #{boardNoticeFileSeq}
AND ta.BOARD_NOTICE_SEQ = #{boardNoticeSeq}
