mapper.select:
SELECT *
FROM TGCA_MAIN_SUB_BANNER
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

mapper.update:
UPDATE TGCA_MAIN_SUB_BANNER
SET PRINT_ORDER= PRINT_ORDER+1,
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

UPDATE TGCA_MAIN_SUB_BANNER
SET PRINT_ORDER= PRINT_ORDER-1,
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

UPDATE TGCA_MAIN_SUB_BANNER
SET DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND SEQ = #{seq}
