mapper.select:
SELECT *
FROM TGAA_MAIN_ROLLING
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

mapper.update:
UPDATE TGAA_MAIN_ROLLING
SET PRINT_ORDER= PRINT_ORDER+1,
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

UPDATE TGAA_MAIN_ROLLING
SET PRINT_ORDER= PRINT_ORDER-1,
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND CODE_LANG_TYPE = #{codeLangType}
AND PRINT_ORDER >= #{printOrder}

UPDATE TGAA_MAIN_ROLLING
SET DELETE_FLAG = 'Y',
	EDIT_DATE = now(),
	EDIT_USER_ID = #{editUserId}
WHERE DELETE_FLAG = 'N'
AND SEQ = #{seq}
